// src/api.js
import axios from "axios";

const API_URL = "http://localhost:4000/api"; // Change to match your backend

export const getTodos = () => axios.get(`${API_URL}/todos`);
export const addTodo = (todo) => axios.post(`${API_URL}/todos`, todo);
export const updateTodo = (id, updates) => axios.put(`${API_URL}/todos/${id}`, updates);
export const deleteTodo = (id) => axios.delete(`${API_URL}/todos/${id}`);

// For authentication (Cognito integration later)
export const login = (credentials) => axios.post(`${API_URL}/auth/login`, credentials);
export const register = (data) => axios.post(`${API_URL}/auth/register`, data);
